import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import './Home.css';

function Home() {
  const [currentSlide, setCurrentSlide] = useState(0);

  // Sample images for the slideshow
  const slides = [
    {
      image: 'https://images.unsplash.com/photo-1612872087720-bb876e2e67d1?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80',
      title: 'Welcome to SmashBet',
      description: 'Your Ultimate Badminton Betting Platform'
    },
    {
      image: 'https://images.unsplash.com/photo-1612872087720-bb876e2e67d1?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80',
      title: 'Live Matches',
      description: 'Bet on exciting badminton matches in real-time'
    },
    {
      image: 'https://images.unsplash.com/photo-1612872087720-bb876e2e67d1?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80',
      title: 'Competitive Odds',
      description: 'Get the best odds for your favorite players'
    }
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);
    return () => clearInterval(timer);
  }, []);

  return (
    <div className="home-page">
      {/* Hero Section with Slideshow */}
      <div className="hero-section position-relative">
        <div className="slideshow-container">
          {slides.map((slide, index) => (
            <motion.div
              key={index}
              className={`slide ${index === currentSlide ? 'active' : ''}`}
              initial={{ opacity: 0 }}
              animate={{ opacity: index === currentSlide ? 1 : 0 }}
              transition={{ duration: 1 }}
              style={{
                backgroundImage: `url(${slide.image})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                height: '100vh',
                width: '100%',
                position: 'absolute',
                top: 0,
                left: 0
              }}
            >
              <div className="slide-content">
                <motion.h1
                  initial={{ y: 50, opacity: 0 }}
                  animate={{ y: 0, opacity: 1 }}
                  transition={{ delay: 0.5 }}
                  className="display-3 text-white text-center mb-4"
                >
                  {slide.title}
                </motion.h1>
                <motion.p
                  initial={{ y: 50, opacity: 0 }}
                  animate={{ y: 0, opacity: 1 }}
                  transition={{ delay: 0.7 }}
                  className="lead text-white text-center mb-5"
                >
                  {slide.description}
                </motion.p>
              </div>
            </motion.div>
          ))}
        </div>

        {/* Overlay */}
        <div className="overlay position-absolute w-100 h-100" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}></div>

        {/* Content */}
        <div className="container position-relative" style={{ zIndex: 1, height: '100vh' }}>
          <div className="row h-100 align-items-center">
            <div className="col-12 text-center">
              <motion.div
                initial={{ y: 50, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ duration: 0.8 }}
              >
                <h1 className="display-1 text-white mb-4">Welcome to SmashBet</h1>
                <p className="lead text-white mb-5">
                  Experience the thrill of badminton betting with the most competitive odds and exciting matches
                </p>
                <motion.div
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.9 }}
                >
                  <Link to="/dashboard" className="btn btn-primary btn-lg px-5 py-3">
                    Get Started
                  </Link>
                </motion.div>
              </motion.div>
            </div>
          </div>
        </div>
      </div>

      {/* Features Section */}
      <div className="features-section py-5 bg-light">
        <div className="container">
          <div className="row">
            <motion.div
              className="col-md-4 mb-4"
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
            >
              <div className="card h-100">
                <div className="card-body text-center">
                  <h3 className="card-title">Live Betting</h3>
                  <p className="card-text">Place bets on ongoing matches with real-time odds updates</p>
                </div>
              </div>
            </motion.div>
            <motion.div
              className="col-md-4 mb-4"
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.2 }}
            >
              <div className="card h-100">
                <div className="card-body text-center">
                  <h3 className="card-title">Secure Platform</h3>
                  <p className="card-text">Your data and transactions are protected with advanced security</p>
                </div>
              </div>
            </motion.div>
            <motion.div
              className="col-md-4 mb-4"
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.4 }}
            >
              <div className="card h-100">
                <div className="card-body text-center">
                  <h3 className="card-title">Expert Analysis</h3>
                  <p className="card-text">Get insights and predictions from badminton experts</p>
                </div>
              </div>
            </motion.div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
