import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { motion } from 'framer-motion';
import api from '../services/api';

function Admin() {
  const { user } = useAuth();
  const [users, setUsers] = useState([]);
  const [matches, setMatches] = useState([]);
  const [activeTab, setActiveTab] = useState('users');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // New match form state
  const [newMatch, setNewMatch] = useState({
    team1: '',
    team2: '',
    date: '',
    tournament: '',
    status: 'upcoming'
  });

  useEffect(() => {
    if (user?.isAdmin) {
      fetchData();
    }
  }, [user]);

  const fetchData = async () => {
    try {
      setLoading(true);
      setError('');

      // Fetch users and matches separately to handle errors individually
      try {
        const usersResponse = await api.get('/admin/users');
        setUsers(usersResponse.data);
      } catch (err) {
        console.error('Error fetching users:', err);
        setError('Failed to fetch users');
      }

      try {
        const matchesResponse = await api.get('/admin/matches');
        setMatches(matchesResponse.data);
      } catch (err) {
        console.error('Error fetching matches:', err);
        setError(prev => prev ? `${prev}. Failed to fetch matches.` : 'Failed to fetch matches');
      }
    } catch (err) {
      console.error('Error in fetchData:', err);
      setError('An unexpected error occurred');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteUser = async (userId) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      try {
        await api.delete(`/admin/users/${userId}`);
        setUsers(users.filter(user => user.id !== userId));
      } catch (err) {
        setError('Failed to delete user');
      }
    }
  };

  const handleDeleteMatch = async (matchId) => {
    if (window.confirm('Are you sure you want to delete this match?')) {
      try {
        await api.delete(`/admin/matches/${matchId}`);
        setMatches(matches.filter(match => match.id !== matchId));
      } catch (err) {
        setError('Failed to delete match');
      }
    }
  };

  const handleAddMatch = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/admin/matches', newMatch);
      setMatches([...matches, response.data]);
      setNewMatch({
        team1: '',
        team2: '',
        date: '',
        tournament: '',
        status: 'upcoming'
      });
    } catch (err) {
      setError('Failed to add match');
    }
  };

  if (!user?.isAdmin) {
    return (
      <div className="container mt-5">
        <div className="alert alert-danger">
          You do not have permission to access this page.
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <h2 className="mb-4">Admin Dashboard</h2>

        {error && (
          <div className="alert alert-danger" role="alert">
            {error}
          </div>
        )}

        <ul className="nav nav-tabs mb-4">
          <li className="nav-item">
            <button
              className={`nav-link ${activeTab === 'users' ? 'active' : ''}`}
              onClick={() => setActiveTab('users')}
            >
              Users
            </button>
          </li>
          <li className="nav-item">
            <button
              className={`nav-link ${activeTab === 'matches' ? 'active' : ''}`}
              onClick={() => setActiveTab('matches')}
            >
              Matches
            </button>
          </li>
        </ul>

        {loading ? (
          <div className="text-center">
            <div className="spinner-border" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </div>
        ) : (
          <>
            {activeTab === 'users' && (
              <div className="card">
                <div className="card-body">
                  <h3 className="card-title mb-4">User Management</h3>
                  <div className="table-responsive">
                    <table className="table">
                      <thead>
                        <tr>
                          <th>Username</th>
                          <th>Email</th>
                          <th>Role</th>
                          <th>Actions</th>
                        </tr>
                      </thead>
                      <tbody>
                        {users.map(user => (
                          <tr key={user.id}>
                            <td>{user.username}</td>
                            <td>{user.email || 'N/A'}</td>
                            <td>{user.isAdmin ? 'Admin' : 'User'}</td>
                            <td>
                              <button
                                className="btn btn-danger btn-sm"
                                onClick={() => handleDeleteUser(user.id)}
                              >
                                Delete
                              </button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'matches' && (
              <div className="card">
                <div className="card-body">
                  <h3 className="card-title mb-4">Match Management</h3>

                  <form onSubmit={handleAddMatch} className="mb-4">
                    <div className="row">
                      <div className="col-md-3">
                        <input
                          type="text"
                          className="form-control"
                          placeholder="Team 1"
                          value={newMatch.team1}
                          onChange={(e) => setNewMatch({...newMatch, team1: e.target.value})}
                          required
                        />
                      </div>
                      <div className="col-md-3">
                        <input
                          type="text"
                          className="form-control"
                          placeholder="Team 2"
                          value={newMatch.team2}
                          onChange={(e) => setNewMatch({...newMatch, team2: e.target.value})}
                          required
                        />
                      </div>
                      <div className="col-md-3">
                        <input
                          type="datetime-local"
                          className="form-control"
                          value={newMatch.date}
                          onChange={(e) => setNewMatch({...newMatch, date: e.target.value})}
                          required
                        />
                      </div>
                      <div className="col-md-2">
                        <select
                          className="form-control"
                          value={newMatch.status}
                          onChange={(e) => setNewMatch({...newMatch, status: e.target.value})}
                        >
                          <option value="upcoming">Upcoming</option>
                          <option value="live">Live</option>
                          <option value="completed">Completed</option>
                        </select>
                      </div>
                      <div className="col-md-1">
                        <button type="submit" className="btn btn-primary">
                          Add
                        </button>
                      </div>
                    </div>
                  </form>

                  <div className="table-responsive">
                    <table className="table">
                      <thead>
                        <tr>
                          <th>Teams</th>
                          <th>Date</th>
                          <th>Status</th>
                          <th>Actions</th>
                        </tr>
                      </thead>
                      <tbody>
                        {matches.map(match => (
                          <tr key={match.id}>
                            <td>{match.team1} vs {match.team2}</td>
                            <td>{new Date(match.date).toLocaleString()}</td>
                            <td>
                              <span className={`badge bg-${match.status === 'live' ? 'danger' : match.status === 'completed' ? 'success' : 'primary'}`}>
                                {match.status}
                              </span>
                            </td>
                            <td>
                              <button
                                className="btn btn-danger btn-sm"
                                onClick={() => handleDeleteMatch(match.id)}
                              >
                                Delete
                              </button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            )}
          </>
        )}
      </motion.div>
    </div>
  );
}

export default Admin;
