import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import '../styles/theme.css';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';

function Dashboard() {
  const { user } = useAuth();
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchMatches();
  }, []);

  const fetchMatches = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await api.get('/matches');
      if (response.data && Array.isArray(response.data)) {
        setMatches(response.data);
      } else {
        setError('Invalid response format from server');
      }
    } catch (err) {
      console.error('Error fetching matches:', err);
      setError('Failed to fetch matches');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="container mt-5 text-center">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <div className="card shadow">
          <div className="card-body">
            <h2 className="card-title mb-4">Welcome, {user?.username}!</h2>
            <div className="row">
              <div className="col-md-4">
                <div className="card bg-primary text-white mb-3">
                  <div className="card-body">
                    <h5 className="card-title">Active Bets</h5>
                    <p className="card-text display-4">0</p>
                  </div>
                </div>
              </div>
              <div className="col-md-4">
                <div className="card bg-success text-white mb-3">
                  <div className="card-body">
                    <h5 className="card-title">Won Bets</h5>
                    <p className="card-text display-4">0</p>
                  </div>
                </div>
              </div>
              <div className="col-md-4">
                <div className="card bg-info text-white mb-3">
                  <div className="card-body">
                    <h5 className="card-title">Total Points</h5>
                    <p className="card-text display-4">0</p>
                  </div>
                </div>
              </div>
            </div>
            <div className="mt-4">
              <h4>Recent Activity</h4>
              <div className="list-group">
                <div className="list-group-item">
                  <p className="mb-0 text-muted">No recent activity</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </motion.div>

      {/* Balance Card */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="card balance-card mb-4"
      >
        <div className="card-body p-4">
          <div className="d-flex justify-content-between align-items-center">
            <div>
              <h6 className="text-white-50 mb-1">Available Balance</h6>
              <h2 className="text-white mb-0">Rs {user?.balance?.toFixed(2) || 0}</h2>
            </div>
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className="btn btn-light"
            >
              Add Funds
            </motion.button>
          </div>
        </div>
      </motion.div>

      {/* Matches Section */}
      <h3 className="mb-4">Upcoming Matches</h3>
      <div className="row">
        {matches.length === 0 ? (
          <div className="col-md-12">
            <div className="alert alert-info">
              No matches available at the moment.
            </div>
          </div>
        ) : (
          matches.map((match, index) => (
            <motion.div
              key={match.id}
              className="col-md-6 col-lg-4 mb-4"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
            >
              <div className="card match-card h-100">
                <div className="card-body">
                  <div className="d-flex justify-content-between align-items-center mb-3">
                    <h5 className="card-title mb-0">Match #{index + 1}</h5>
                    <span className="badge bg-primary">
                      {match.teamA?.name} VS {match.teamB?.name}
                    </span>
                  </div>
                  <div className="text-center mb-4">
                    <div className="d-flex justify-content-between align-items-center">
                      <div className="text-center flex-grow-1">
                        <h6 className="mb-2">{match.teamA?.name}</h6>
                        <span className="badge bg-light text-dark">Odds: 2.0</span>
                      </div>
                      <div className="mx-3">VS</div>
                      <div className="text-center flex-grow-1">
                        <h6 className="mb-2">{match.teamB?.name}</h6>
                        <span className="badge bg-light text-dark">Odds: 2.0</span>
                      </div>
                    </div>
                  </div>
                  <div className="text-center mt-2">
                    <span className={`badge bg-${match.isBettingOpen ? 'success' : 'secondary'}`}>
                      {match.isBettingOpen ? 'Betting Open' : 'Betting Closed'}
                    </span>
                  </div>
                  <hr />
                  <motion.div
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                  >
                    <Link 
                      to={`/match/${match.id}`} 
                      className="btn btn-primary w-100"
                    >
                      Place Bet
                    </Link>
                  </motion.div>
                </div>
              </div>
            </motion.div>
          ))
        )}
      </div>
    </div>
  );
}

export default Dashboard;
