import React from 'react';
import { Link } from 'react-router-dom';

function Dashboard() {
  // Mock data for upcoming matches
  const upcomingMatches = [
    { id: 1, player1: 'John Doe', player2: 'Jane Smith', date: '2024-03-20', time: '14:00' },
    { id: 2, player1: 'Mike Johnson', player2: 'Sarah Williams', date: '2024-03-21', time: '15:30' },
    { id: 3, player1: 'David Brown', player2: 'Emily Davis', date: '2024-03-22', time: '16:00' },
  ];

  return (
    <div className="container mt-4">
      <h2>Upcoming Matches</h2>
      <div className="row">
        {upcomingMatches.map(match => (
          <div key={match.id} className="col-md-4 mb-4">
            <div className="card">
              <div className="card-body">
                <h5 className="card-title">Match #{match.id}</h5>
                <p className="card-text">
                  {match.player1} vs {match.player2}
                </p>
                <p className="card-text">
                  <small className="text-muted">
                    Date: {match.date} | Time: {match.time}
                  </small>
                </p>
                <Link to={`/match/${match.id}`} className="btn btn-primary">
                  View Details
                </Link>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Dashboard;
