import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import './Home.css';

function Home() {
  const [currentSlide, setCurrentSlide] = useState(0);

  // Updated slides with local images
  const slides = [
    {
      image: '/img1.jpg',
      title: 'Welcome to SmashBet',
      description: 'Your Ultimate Badminton Betting Platform'
    },
    {
      image: '/img2.jpg',
      title: 'Live Matches',
      description: 'Bet on exciting badminton matches in real-time'
    },
    {
      image: '/img5.jpg',
      title: 'Competitive Odds',
      description: 'Get the best odds for your favorite players'
    }
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);
    return () => clearInterval(timer);
  }, []);

  return (
    <div className="home-page">
      {/* Hero Section with Slideshow */}
      <div className="hero-section position-relative">
        <div className="slideshow-container">
          {slides.map((slide, index) => (
            <motion.div
              key={index}
              className={`slide ${index === currentSlide ? 'active' : ''}`}
              initial={{ opacity: 0, scale: 1.1 }}
              animate={{ 
                opacity: index === currentSlide ? 1 : 0,
                scale: index === currentSlide ? 1 : 1.1
              }}
              transition={{ duration: 1.5, ease: "easeInOut" }}
              style={{
                backgroundImage: `url(${slide.image})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                height: '100vh',
                width: '100%',
                position: 'absolute',
                top: 0,
                left: 0
              }}
            >
              <div className="slide-content position-absolute w-100 h-100 d-flex flex-column justify-content-center align-items-center">
                <motion.h1
                  initial={{ y: 50, opacity: 0 }}
                  animate={{ y: 0, opacity: 1 }}
                  transition={{ delay: 0.5, type: "spring", stiffness: 100 }}
                  className="display-1 text-white text-center mb-4"
                >
                  {slide.title}
                </motion.h1>
                <motion.p
                  initial={{ y: 50, opacity: 0 }}
                  animate={{ y: 0, opacity: 1 }}
                  transition={{ delay: 0.7, type: "spring", stiffness: 100 }}
                  className="lead text-white text-center mb-5"
                >
                  {slide.description}
                </motion.p>
              </div>
            </motion.div>
          ))}
        </div>

        {/* Overlay */}
        <div className="overlay position-absolute w-100 h-100"></div>

        {/* Hero Content */}
        <div className="hero-content position-relative z-3">
          <div className="container">
            <div className="row h-100 align-items-center">
              <div className="col-12 text-center">
                <motion.div
                  initial={{ y: 50, opacity: 0 }}
                  animate={{ y: 0, opacity: 1 }}
                  transition={{ duration: 0.8, type: "spring", stiffness: 100 }}
                >
                  <h1 className="display-1 text-white mb-4">Welcome to SmashBet</h1>
                  <p className="lead text-white mb-5">
                    Experience the thrill of badminton betting with the most competitive odds and exciting matches
                  </p>
                  <motion.div
                    whileHover={{ scale: 1.1, rotate: 2 }}
                    whileTap={{ scale: 0.9, rotate: -2 }}
                  >
                    <Link to="/dashboard" className="btn btn-primary btn-lg px-5 py-3">
                      Get Started
                    </Link>
                  </motion.div>
                </motion.div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Features Section */}
      <div className="features-section py-5 bg-light">
        <div className="container">
          <motion.h2 
            className="text-center mb-5"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
          >
            Why Choose SmashBet?
          </motion.h2>
          <div className="row">
            <motion.div
              className="col-md-4 mb-4"
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
            >
              <div className="card h-100">
                <div className="card-body text-center">
                  <motion.div
                    whileHover={{ scale: 1.1 }}
                    className="feature-icon mb-3"
                  >
                    ðŸŽ¯
                  </motion.div>
                  <h3 className="card-title">Live Betting</h3>
                  <p className="card-text">Place bets on ongoing matches with real-time odds updates</p>
                </div>
              </div>
            </motion.div>
            <motion.div
              className="col-md-4 mb-4"
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.2 }}
            >
              <div className="card h-100">
                <div className="card-body text-center">
                  <motion.div
                    whileHover={{ scale: 1.1 }}
                    className="feature-icon mb-3"
                  >
                    ðŸ”’
                  </motion.div>
                  <h3 className="card-title">Secure Platform</h3>
                  <p className="card-text">Your data and transactions are protected with advanced security</p>
                </div>
              </div>
            </motion.div>
            <motion.div
              className="col-md-4 mb-4"
