import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { motion } from 'framer-motion';
import api from '../services/api';

function Admin() {
  const { user } = useAuth();
  const [users, setUsers] = useState([]);
  const [matches, setMatches] = useState([]);
  const [activeTab, setActiveTab] = useState('users');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // New match form state
  const [newMatch, setNewMatch] = useState({
    team1: '',
    team2: '',
    tournament: '',
    isBettingOpen: true
  });

  // Edit match state
  const [editingMatch, setEditingMatch] = useState(null);

  useEffect(() => {
    if (user?.isAdmin) {
      fetchData();
    }
  }, [user]);

  const fetchData = async () => {
    try {
      setLoading(true);
      setError('');

      // Fetch users and matches separately to handle errors individually
      try {
        const usersResponse = await api.get('/admin/users');
        setUsers(usersResponse.data);
      } catch (err) {
        console.error('Error fetching users:', err);
        setError('Failed to fetch users');
      }

      try {
        const matchesResponse = await api.get('/admin/matches');
        setMatches(matchesResponse.data);
      } catch (err) {
        console.error('Error fetching matches:', err);
        setError(prev => prev ? `${prev}. Failed to fetch matches.` : 'Failed to fetch matches');
      }
    } catch (err) {
      console.error('Error in fetchData:', err);
      setError('An unexpected error occurred');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteUser = async (userId) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      try {
        await api.delete(`/admin/users/${userId}`);
        setUsers(users.filter(user => user.id !== userId));
      } catch (err) {
        setError('Failed to delete user');
      }
    }
  };

  const handleDeleteMatch = async (matchId) => {
    if (window.confirm('Are you sure you want to delete this match?')) {
      try {
        await api.delete(`/admin/matches/${matchId}`);
        setMatches(matches.filter(match => match.id !== matchId));
      } catch (err) {
        setError('Failed to delete match');
      }
    }
  };

  const handleAddMatch = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post('/admin/matches', {
        team1: newMatch.team1,
        team2: newMatch.team2,
        tournament: newMatch.tournament || 'Regular Match'
      });
      setMatches([...matches, response.data]);
      setNewMatch({
        team1: '',
        team2: '',
        tournament: '',
        isBettingOpen: true
      });
    } catch (err) {
      console.error('Error adding match:', err);
      setError('Failed to add match: ' + (err.response?.data?.error || err.message));
    }
  };

  const handleEditMatch = async (e) => {
    e.preventDefault();
    try {
      const response = await api.put(`/admin/matches/${editingMatch.id}`, {
        team1: editingMatch.teamA.name,
        team2: editingMatch.teamB.name,
        tournament: editingMatch.tournament,
        isBettingOpen: editingMatch.isBettingOpen
      });
      setMatches(matches.map(match => 
        match.id === editingMatch.id ? response.data : match
      ));
      setEditingMatch(null);
    } catch (err) {
      console.error('Error updating match:', err);
      setError('Failed to update match: ' + (err.response?.data?.error || err.message));
    }
  };

  if (!user?.isAdmin) {
    return (
      <div className="container mt-5">
        <div className="alert alert-danger">
          You do not have permission to access this page.
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <h2 className="mb-4">Admin Dashboard</h2>

        {error && (
          <div className="alert alert-danger" role="alert">
            {error}
          </div>
        )}

        <ul className="nav nav-tabs mb-4">
          <li className="nav-item">
            <button
              className={`nav-link ${activeTab === 'users' ? 'active' : ''}`}
              onClick={() => setActiveTab('users')}
            >
              Users
            </button>
          </li>
          <li className="nav-item">
            <button
              className={`nav-link ${activeTab === 'matches' ? 'active' : ''}`}
              onClick={() => setActiveTab('matches')}
            >
              Matches
            </button>
          </li>
        </ul>

        {loading ? (
          <div className="text-center">
            <div className="spinner-border" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
          </div>
        ) : (
          <>
            {activeTab === 'users' && (
              <div className="card">
                <div className="card-body">
                  <h3 className="card-title mb-4">User Management</h3>
                  <div className="table-responsive">
                    <table className="table">
                      <thead>
                        <tr>
                          <th>Username</th>
                          <th>Email</th>
                          <th>Role</th>
                          <th>Actions</th>
                        </tr>
                      </thead>
                      <tbody>
                        {users.map(user => (
                          <tr key={user.id}>
                            <td>{user.username}</td>
                            <td>{user.email || 'N/A'}</td>
                            <td>{user.isAdmin ? 'Admin' : 'User'}</td>
                            <td>
                              <button
                                className="btn btn-danger btn-sm"
                                onClick={() => handleDeleteUser(user.id)}
                              >
                                Delete
                              </button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'matches' && (
              <div className="card">
                <div className="card-body">
                  <h3 className="card-title mb-4">Match Management</h3>

                  <form onSubmit={handleAddMatch} className="mb-4">
                    <div className="row">
                      <div className="col-md-4">
                        <input
                          type="text"
                          className="form-control"
                          placeholder="Team 1"
                          value={newMatch.team1}
                          onChange={(e) => setNewMatch({...newMatch, team1: e.target.value})}
                          required
                        />
                      </div>
                      <div className="col-md-4">
                        <input
                          type="text"
                          className="form-control"
                          placeholder="Team 2"
                          value={newMatch.team2}
                          onChange={(e) => setNewMatch({...newMatch, team2: e.target.value})}
                          required
                        />
                      </div>
                      <div className="col-md-3">
                        <input
                          type="text"
                          className="form-control"
                          placeholder="Tournament"
                          value={newMatch.tournament}
                          onChange={(e) => setNewMatch({...newMatch, tournament: e.target.value})}
                          required
                        />
                      </div>
                      <div className="col-md-1">
                        <button type="submit" className="btn btn-primary">
                          Add
                        </button>
                      </div>
                    </div>
                  </form>

                  {editingMatch && (
                    <form onSubmit={handleEditMatch} className="mb-4">
                      <div className="row">
                        <div className="col-md-3">
                          <input
                            type="text"
                            className="form-control"
                            placeholder="Team 1"
                            value={editingMatch.teamA.name}
                            onChange={(e) => setEditingMatch({
                              ...editingMatch,
                              teamA: { ...editingMatch.teamA, name: e.target.value }
                            })}
                            required
                          />
                        </div>
                        <div className="col-md-3">
                          <input
                            type="text"
                            className="form-control"
                            placeholder="Team 2"
                            value={editingMatch.teamB.name}
                            onChange={(e) => setEditingMatch({
                              ...editingMatch,
                              teamB: { ...editingMatch.teamB, name: e.target.value }
                            })}
                            required
                          />
                        </div>
                        <div className="col-md-3">
                          <input
                            type="text"
                            className="form-control"
                            placeholder="Tournament"
                            value={editingMatch.tournament}
                            onChange={(e) => setEditingMatch({
                              ...editingMatch,
                              tournament: e.target.value
                            })}
                            required
                          />
                        </div>
                        <div className="col-md-2">
                          <div className="form-check form-switch">
                            <input
                              className="form-check-input"
                              type="checkbox"
                              checked={editingMatch.isBettingOpen}
                              onChange={(e) => setEditingMatch({
                                ...editingMatch,
                                isBettingOpen: e.target.checked
                              })}
                            />
                            <label className="form-check-label">Betting Open</label>
                          </div>
                        </div>
                        <div className="col-md-1">
                          <button type="submit" className="btn btn-success">
                            Save
                          </button>
                        </div>
                      </div>
                    </form>
                  )}

                  <div className="table-responsive">
                    <table className="table">
                      <thead>
                        <tr>
                          <th>Teams</th>
                          <th>Tournament</th>
                          <th>Status</th>
                          <th>Actions</th>
                        </tr>
                      </thead>
                      <tbody>
                        {matches.map(match => (
                          <tr key={match.id}>
                            <td>{match.teamA?.name || 'Team A'} vs {match.teamB?.name || 'Team B'}</td>
                            <td>{match.tournament}</td>
                            <td>
                              <span className={`badge bg-${match.isBettingOpen ? 'success' : 'secondary'}`}>
                                {match.isBettingOpen ? 'Open' : 'Closed'}
                              </span>
                            </td>
                            <td>
                              <button
                                className="btn btn-primary btn-sm me-2"
                                onClick={() => setEditingMatch(match)}
                              >
                                Edit
                              </button>
                              <button
                                className="btn btn-danger btn-sm"
                                onClick={() => handleDeleteMatch(match.id)}
                              >
                                Delete
                              </button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            )}
          </>
        )}
      </motion.div>
    </div>
  );
}

export default Admin;
